import "./index.css";
import "./base.css"
import {useState} from "react"
import Swal from 'sweetalert2'

function Login() {
    
    const [email, setEmail] = useState();
    const [senha, setSenha] = useState();
    const autenticar = () => {
        if(!email || !senha){
            Swal.fire({
                icon: "error",
                text: "Os campos de email e senha sao obrigatorios!",
              });
        }
    }
    return (
        <div className="caixa-login">


            <div className="titulo-login">
                <h1 >Login</h1>
            </div>


            <div className="grupo">
                <label for="email">E-mail:</label> <br/>
                <input id="email" value={email} onChange={(e) => e.target.value} type="text" placeholder="Digite seu e-mail"/><br/><br/>
                </div>


                    <div className="grupo">
                        <label for="senha">Senha:</label> <br/>
                            <input id="senha" value={senha} type="passoword" placeholder="Digite sua senha"/> <br/><br/>
                    </div>


                    <div className="esqueci-minha-senha">
                        <a href="#">Esqueci minha senha</a>
                    </div>
                    <br/>
                    <button id="btn-entrar" onClick={autenticar}>Entrar</button>
                </div>
                    );
};
export default Login;
